import "./Contador.css";
import React from "react";

export default class Contador extends React.Component{
state = {
    numero: this.props.numeroInicial || 0,
    passo: this.props.passoInicial || 5,
};

inc = () => {
    this.setState({
        numero:this.state.numero + this.state.passo,
    })
}

dec = () => {
    this.setState({
        numero: this.state.numero - this.state.passo,
    })
    
}
}
